def count_good_substrings(n: int, s: str) -> int:
    """"""
    ret = n * (n - 1) // 2
    for x in range(2):
        cur = 1
        for i in range(1, n):
            if s[i - 1] == s[i]:
                cur += 1
            else:
                ret -= cur - x
                cur = 1
        s = s[::-1]
    return ret
