def replace_forbidden_substrings(n, forbidden_substrings, original_string, lucky_letter):
    w = list(original_string)
    m = len(w)
    z = []
    i = 1
    while i < m:
        for forbidden in forbidden_substrings:
            if w[i].lower() == forbidden[0].lower():
                if i + len(forbidden) <= m:
                    f = 1
                    for k in range(i, i + len(forbidden)):
                        if w[k].lower() != forbidden[k - i].lower():
                            f = 0
                            break
                    if f:
                        if len(z) != 0:
                            if z[-1][1] >= i:
                                z[-1][1] = max(i + len(forbidden) - 1, z[-1][1])
                            else:
                                z.append([i, i + len(forbidden) - 1])
                        else:
                            z.append([i, i + len(forbidden) - 1])
        i += 1
    for i in z:
        for k in range(i[0], i[1] + 1):
            if w[k].isupper():
                if w[k] != lucky_letter.upper():
                    w[k] = lucky_letter.upper()
                elif w[k] != 'A':
                    w[k] = 'A'
                else:
                    w[k] = 'B'
            elif w[k] != lucky_letter.lower():
                w[k] = lucky_letter.lower()
            elif w[k] != 'a':
                w[k] = 'a'
            else:
                w[k] = 'b'
    return ''.join(w)

