def min_operations_to_equalize_array(arr, N):
    total_sum = sum(arr)
    if total_sum % N == 0:
        return -1
    target_value = total_sum // N
    total_difference_sum = 0
    for value in arr:
        total_difference_sum += abs(value - target_value)
    return total_difference_sum // 2

