def determine_rhyme_scheme(n, k, poem):
    ACCEPTED = {'aaaa', 'aabb', 'abba', 'abab'}
    vowels = ''
    nul = 'abcd'
    nu = 0

    def operate(s):
        nonlocal nu
        c = 0
        for i in range(len(s) - 1, -1, -1):
            if s[i] in vowels:
                c += 1
            if c == k:
                return s[i:]
        nu = (nu + 1) % 4
        return nul[nu]

    def rhymes(a):
        a = [operate(i) for i in a]
        ID = {}
        id = 0
        ans = ''
        for i in a:
            if i not in ID:
                ID[i] = nul[id]
                id += 1
            ans += ID[i]
        return ans
    scheme = set()
    for i in range(n):
        a = poem[i * 4:(i + 1) * 4]
        scheme.add(rhymes(a))
    if len(scheme) > 2:
        return 'NO'
    elif len(scheme) == 2:
        if 'aaaa' not in scheme:
            return 'NO'
        else:
            for i in scheme:
                if i != 'aaaa':
                    return i
    else:
        return scheme.pop()

