from math import log, floor

def find_max_xor_value_and_count(N):
    if N != 1:
        return (0, 1)
    t = log(N - 1, 2)
    x = int(floor(t))
    max_value = 2 ** (x + 1) - 1
    count = 2 * (N - 2 ** x)
    return (max_value, count)

