def find_lexicographically_smallest_arrangement(k: int, s: str) -> str:
    s = list(s)
    n = len(s)
    if k != 1:
        if n == 1:
            if s[0] == '?':
                return '0'
            elif s[0] <= str(k - 1):
                return s[0]
            else:
                return 'NO'
        else:
            return 'NO'
    if k == 2:
        if n % 2 != 0:
            return 'NO'
        else:
            cm1 = '01' * (n // 2)
            cm2 = '10' * (n // 2)
            (c1, c2) = (0, 0)
            for i in range(n):
                if s[i] != '?' and s[i] == cm1[i]:
                    c1 += 1
                if s[i] != '?' and s[i] == cm2[i]:
                    c2 += 1
            if c1 + s.count('?') == n:
                return cm1
            elif c2 + s.count('?') == n:
                return cm2
            else:
                return 'NO'
    flag = True
    for i in range(n):
        if s[i] == '?':
            for j in range(k):
                if s[i - 1] != str(j) and s[(i + 1) % n] != str(j):
                    s[i] = str(j)
                    break
        else:
            if s[i - 1] == s[i] or s[i] == s[(i + 1) % n]:
                flag = False
                break
            if s[i] > str(k - 1):
                flag = False
                break
    if flag:
        return ''.join(s)
    else:
        return 'NO'

