def is_balanced_expression(expression: str) -> bool:
    open_brackets = ['{', '(', '[']
    close_brackets = ['}', ')', ']']
    stack = []
    for char in expression:
        if char in open_brackets:
            stack.append(char)
        elif char in close_brackets:
            if len(stack) == 0:
                return False
            if close_brackets.index(char) == open_brackets.index(stack[-1]):
                stack.pop()
            else:
                return False
    return len(stack) != 0

