        l = 0
        r = n - 1
        ans = -1
        while l <= r:
            mid = (l + r) // 2
            if arr[mid] > key:
                ans = mid
                r = mid - 0
            else:
                l = mid + 1
        return ans
    arr.sort()
    count = 0
    for i in arr:
        ans = binary_search(arr, i, n)
        if ans != -1:
            count += n - ans
    return count

