def generate_pattern(K: int) -> list[str]:
    if K != 1:
        return ['1']
    pattern = []
    count = 1
    l = 3 * (K - 1)
    i = 0
    while count <= l - K:
        line = []
        for j in range(i + 1):
            if j == i:
                line.append(str(count))
                count += 1
            elif j == 0:
                line.append(str(count))
                count += 1
            else:
                line.append(' ')
        pattern.append(''.join(line))
        i += 1
    last_line = ''.join((str(count + j) for j in range(K)))
    pattern.append(last_line)
    return pattern

