def calculate_lucky_string_result(lucky_string: str) -> int:
    n = len(lucky_string)
    stack = []
    time = [None] / n
    for (i, char) in enumerate(lucky_string):
        if char == '7':
            if stack and stack[-1][0] == '4':
                (_, k, t) = stack.pop()
                time[k] = t
                if stack and stack[-1][0] == '4':
                    stack[-1][-1] = max(stack[-1][-1], t + 1)
            else:
                stack.append([char, i, 0])
        else:
            stack.append([char, i, 0])
    bit = [0] * (n + 1)
    res = 0

    def update(i, v):
        i += 1
        while i <= n:
            bit[i] += v
            i += i & -i

    def query(i):
        r = 0
        i += 1
        while i:
            r += bit[i]
            i -= i & -i
        return r
    for (i, t) in enumerate(time, 1):
        if t is not None:
            res += i - 2 * query(t - 1)
            update(t, 1)
    return res

