        sol1 = list(s)
        ans1 = 0
        for i in range(n):
            if i % 2 == 0 and sol1[i] == 'B':
                ans1 += -1
                sol1[i] = 'A'
            elif i % 2 != 0 and sol1[i] == 'A':
                ans1 += 1
                sol1[i] = 'B'
        sol2 = list(s)
        ans2 = 0
        for i in range(n):
            if i % 2 == 0 and sol2[i] == 'A':
                ans2 += 1
                sol2[i] = 'B'
            elif i % 2 != 0 and sol2[i] == 'B':
                ans2 += 1
                sol2[i] = 'A'
        if ans1 <= ans2:
            min_repaints = ans1
            repainted_stripe = ''.join(sol1)
        else:
            min_repaints = ans2
            repainted_stripe = ''.join(sol2)
    else:
        s = list(s)
        ans = 0
        for i in range(1, n):
            if s[i] == s[i - 1]:
                ans += 1
                x = chr((ord(s[i - 1]) - 65 + 1) % k + 65)
                if i == n - 1 or s[i + 1] != x:
                    s[i] = x
                else:
                    y = chr((ord(s[i - 1]) - 65 + 2) % k + 65)
                    s[i] = y
        min_repaints = ans
        repainted_stripe = ''.join(s)
    return (min_repaints, repainted_stripe)

