def max_digit_sum_of_product(test_cases):

    def digsum(s):
        return sum((int(d) for d in s))
    results = []
    for (n, sequence) in test_cases:
        max_sum = 1
        for i in range(n - 1):
            for j in range(i + 1, n):
                product = sequence[i] * sequence[j]
                product_sum = digsum(str(product))
                if max_sum < product_sum:
                    max_sum = product_sum
        results.append(max_sum)
    return results

