def calculate_min_operations(a: int, b: int) -> int:
    """"""
    count = 0
    while True:
        if a == 1 and b == 1:
            break
        if a == 1:
            count += b - 1
            break
        if b == 1:
            count += a - 1
            break
        if a > b:
            count += a // b
            a = a % b
        else:
            count += b // a
            b = b % a
        if a == 0 or b == 0:
            break
    return count
