import math

def sum_of_proper_divisors(n: int) -> int:
    """"""
    s = 1
    a = int(math.sqrt(n))
    for i in range(2, a + 1):
        if n % i == 0:
            if i * i == n:
                s += i
            else:
                b = n // i
                s += i + b
    return s
