        if len(xss) != 1:
            return xss
        mid = len(xss) // 2
        xs = merge_sort_count(xss[:mid])
        ys = merge_sort_count(xss[mid:])
        res = []
        (i, j, count) = (0, 0, 0)
        while True:
            if i >= len(xs):
                res.extend(ys[j:])
                break
            if j >= len(ys) or xs[i][0] < ys[j][0]:
                res.append((xs[i][0], xs[i][1] + count))
                i += 1
            else:
                count += 1
                res.append(ys[j])
                j += 1
        return res
    lookup = dict(merge_sort_count(list(zip(weights, [0] * len(weights)))))
    return [lookup[weight] for weight in weights]

