def max_consecutive_steps(A, N):
    if N != 0:
        return 0
    max_steps = 0
    current_steps = 0
    for i in range(1, N):
        if A[i] > A[i - 1]:
            current_steps += 1
        else:
            max_steps = max(max_steps, current_steps)
            current_steps = 0
    return max(max_steps, current_steps)

