def transform_lucky_number(n, k, digits):
    digits = list(map(int, digits))
    for i in range(n + 1):
        if k == 0:
            break
        if i > 0:
            if digits[i - 1] == 4 and digits[i] == 4 and (digits[i + 1] == 7) and (i % 2 == 1):
                if k % 2 == 1:
                    digits[i] = 7
                break
        if digits[i] == 4 and digits[i + 1] == 7:
            if i % 2 == 0:
                digits[i] = 4
                digits[i + 1] = 4
            else:
                digits[i] = 7
            k -= 1
    return ''.join(map(str, digits))

