def shortest_time_to_gather_chestnuts(m, n, k, T, P):
    t_min = 1
    t_max = 10000000
    t_ans = 0
    while t_min <= t_max:
        t_mid = (t_min + t_max) // 2
        fa = [(t_mid - T[i]) // P[i] + 1 for i in range(m) if t_mid >= T[i]]
        fm = sum(sorted(fa, reverse=True)[:n])
        if fm < k:
            t_min = t_mid + 1
        else:
            t_max = t_mid - 1
        if fm >= k:
            t_ans = t_mid
    return t_ans

