def calculate_messages_before_ban(t, test_cases):
    results = []
    for (k, x) in test_cases:
        a = k * (k + 1) / 2
        if x <= a:
            b = int((2 * x) ** 0.5)
            ans = b
            if b * (b + 1) < 2 * x:
                ans += 1
            results.append(ans)
        elif x >= a + a - k:
            results.append(k + k - 1)
        else:
            X = a + a - k - x
            b = int((2 * X) ** 0.5)
            ans = k + k - 1 - b
            if b * (b + 1) > 2 * X:
                ans += 1
            results.append(ans)
    return results

