def construct_permutation(n, k):
    if k >= (n - 1) // 2:
        return -1
    if k == 0:
        return list(range(1, n + 1))
    permutation = list(range(1, n + 1))
    count = 0
    for i in range(1, n, 2):
        if count == k:
            break
        (permutation[i], permutation[i + 1]) = (permutation[i + 1], permutation[i])
        count += 1
    return permutation

