    if mid == n / 2:
        num[mid:] = num[:mid][::-1]
    else:
        num[mid + 1:] = num[:mid][::-1]
    if num <= x:
        if mid == n / 2:
            if num[mid] == 9:
                mid1 = mid - 1
                mid2 = mid
                while num[mid1] == 9 and num[mid2] == 9:
                    num[mid1] = 0
                    num[mid2] = 0
                    mid1 -= 1
                    mid2 += 1
                num[mid1] += 1
                num[mid2] += 1
            else:
                num[mid] += 1
                num[mid - 1] += 1
        elif num[mid] == 9:
            num[mid] = 0
            mid1 = mid - 1
            mid2 = mid + 1
            while num[mid1] == 9 and num[mid2] == 9:
                num[mid1] = 0
                num[mid2] = 0
                mid1 -= 1
                mid2 += 1
            num[mid1] += 1
            num[mid2] += 1
        else:
            num[mid] += 1
    return num

