            if c == '(':
                st.update(currentit, 1)
            elif c == ')':
                st.update(currentit, --1)
            else:
                st.update(currentit, 0)
        vmax = st.tmax[1]
        vmin = st.tmin[1]
        vsum = st.tsum[1]
        if vmin >= 0 and vsum == 0:
