from operator import itemgetter

def calculate_singer_scores(test_cases):
    results = []
    for singers in test_cases:
        n = len(singers)
        start = []
        end = []
        for (i, (L_i, U_i)) in enumerate(singers):
            start.append((L_i, i))
            end.append((U_i, i))
        score = [0] / n
        start.sort(key=itemgetter(0))
        end.sort(key=itemgetter(0), reverse=True)
        for i in range(n - 1):
            score[start[i][1]] += n - i - 1
            score[end[i][1]] += n - i - 1
        results.append(score)
    return results

