        return -1
    exists = True
    flag = 0
    prev = shoe_sizes[0]
    perms = []
    for j in range(1, n):
        if shoe_sizes[j] == prev:
            perms.append(j + 1)
            continue
        if flag == j - 1:
            exists = False
            break
        perms.append(flag + 1)
        prev = shoe_sizes[j]
        flag = j
    if flag == n - 1:
        exists = False
    perms.append(flag + 2)
    if not exists:
        return -1
    else:
        return perms

