from itertools import combinations
from math import hypot

def is_peaceful_yard(yard, min_distance):
    cats = [(i, j) for (i, row) in enumerate(yard) for (j, cell) in enumerate(row) if cell in 'LMR']
    if len(cats) < 1:
        return True
    for (p, q) in combinations(cats, 2):
        if hypot(q[0] - p[0], q[1] - p[1]) < min_distance:
            return False
    return True

