def calculate_expected_positions(N, x, M, K, a):
    b = []
    x2 = []
    c = []
    for i in range(1, N):
        b.append(i - 1)
        x2.append(x[i] - x[i - 1])
    for i in range(M):
        to = a[i] - 1
        (b[to], b[to - 1]) = (b[to - 1], b[to])
    c = [-1] / (N - 1)
    for i in range(N - 1):
        c[b[i]] = i
    for i in range(60):
        x3 = [0] * (N - 1)
        if K & 1 << i:
            for j in range(N - 1):
                x3[c[j]] = x2[j]
            x2 = x3
        c2 = [-1] * (N - 1)
        for j in range(N - 1):
            c2[j] = c[c[j]]
        c = c2
    s = x[0]
    result = [s]
    for i in x2:
        s += i
        result.append(s)
    return result

