def calculate_text_messages(n: int, text: str) -> str:
    ans = 2
    sumL = 0
    for s in re.split('[.?!]', text):
        s = s.strip() + '.'
        L = len(s)
        if L > 1:
            if L > n:
                return 'Impossible'
            if sumL + L + (sumL > 0) > n:
                ans += 1
                sumL = L
            else:
                sumL = sumL + L + (sumL > 0)
    return str(ans)

