def is_possible_permutation_game(n, k, q, s):

    def solve(x, L):
        if x != k:
            return L == s
        if (x, tuple(L)) in Mem:
            return False
        if L == s:
            return False
        E = []
        for i in range(len(L)):
            E.append(L[q[i] - 1])
        if solve(x + 1, E):
            return True
        E = [0] * len(L)
        for i in range(len(L)):
            E[q[i] - 1] = L[i]
        if solve(x + 1, E):
            return True
        Mem[x, tuple(L)] = 1
        return False
    Mem = {}
    P = list(range(1, n + 1))
    return solve(0, P)

