    aSum = [0] * n
    kBatchSum = [0] * n
    for (i, x) in enumerate(a):
        aSum[i] = x
        if i > 0:
            aSum[i] += aSum[i - 1]
        kBatchSum[i] = a[i]
        if i - k >= 0:
            kBatchSum[i] += kBatchSum[i - k]
        if i + 1 - k < 0:
            kBatchSum[i] = 0

    def getMinCost(nItems):
        idx = nItems - 1
        cost = 0
        if idx >= k - 1:
            cost -= kBatchSum[idx]
            idx = (idx + 1) % k - 1
        if idx >= 0:
            cost += aSum[idx]
        return cost
    ans = 0
    for nItems in range(n + 1):
        if getMinCost(nItems) <= p:
            ans = nItems
    return ans

