def decode_cipher(k: int, s: str) -> str:
    n = len(s) - k - 1
    solution = [0] * n
    xor_solution = 0
    for i in range(n):
        if i >= k:
            xor_solution ^= solution[i - k]
        solution[i] = int(s[i]) ^ xor_solution
        xor_solution ^= solution[i]
    return ''.join(map(str, solution))

