def decode_at_index(S: str, K: int) -> str:
    size = 1
    for c in S:
        if c.isdigit():
            size *= int(c)
        else:
            size += 1
    for c in reversed(S):
        K %= size
        if K == 0 and c.isalpha():
            return c
        if c.isdigit():
            size //= int(c)
        else:
            size -= 1

