def calculate_min_cost_to_vowels(test_cases):
    alphabet_positions = {chr(i + 96): i for i in range(1, 27)}
    vowels = ['', 'e', 'i', 'o', 'u']
    result = []
    for string in test_cases:
        count = 0
        for char in string:
            if char not in vowels:
                min_cost = min((abs(alphabet_positions[char] - alphabet_positions[v]) for v in vowels))
                count += min_cost
        result.append(count)
    return result

