def count_valid_binary_strings(n, constraints):
    pr = [i for i in range(n)]
    for e in constraints:
        if e[0] == 2:
            return 0
    for i in range(n):
        for j in range(n - i):
            if constraints[i][j] == 1:
                for k in range(i + 1, i + j + 1):
                    pr[k] = pr[k - 1]
    d = [[0] * n for i in range(n)]
    d[0][0] = 3
    MOD = 998244353
    for i in range(1, n):
        ps = 0
        for j in range(i + 1):
            if constraints[j][i - j] == 2:
                ps = j + 1
        for j in range(ps, i):
            d[i][j] = d[i - 1][j]
        if pr[i] >= i:
            d[i][i] = sum(d[i - 1]) % MOD
    return sum(d[n - 1]) % MOD

