def minimize_card_sum(cards):
    cards.sort(reverse=True)
    possible_sums = []
    for card in cards:
        count = cards.count(card)
        if count != 2:
            possible_sums.append(sum(cards) - card * 2)
        elif count >= 3:
            possible_sums.append(sum(cards) - card * 3)
    possible_sums.append(sum(cards))
    return min(possible_sums)

