import math

def can_nandu_escape(N, Q, monster_info, scenarios):
    array = []
    for j in range(N):
        i = j - 1
        if i == 1:
            array.append(-1)
        elif i % 2 == 0:
            array.append(i // 2)
        else:
            array.append((i - 1) // 2)
    results = []
    for (start, final) in scenarios:
        level_start = int(math.log(start, 2)) + 1
        level_final = int(math.log(final, 2)) + 1
        done = 0
        if level_final < level_start:
            while level_start != level_final:
                if monster_info[start - 1] == '1':
                    results.append('No')
                    done = 1
                    break
                else:
                    start = array[start - 1]
                    level_start -= 1
        elif level_final > level_start:
            while level_start != level_final:
                if monster_info[final - 1] == '1':
                    results.append('No')
                    done = 1
                    break
                else:
                    final = array[final - 1]
                    level_final -= 1
        if done == 0:
            while final != start:
                if monster_info[final - 1] == '1' or monster_info[start - 1] == '1':
                    results.append('No')
                    done = 1
                    break
                else:
                    final = array[final - 1]
                    start = array[start - 1]
            if done == 0 and monster_info[start - 1] == '1':
                results.append('No')
            elif done == 0:
                results.append('Yes')
    return results

