def find_largest_pile_of_identical_characters(s: str) -> int:
    t = {}
    for (i, c) in enumerate(s):
        if c in t:
            t[c] = (i, 1)
        elif t[c][0] - i & 1:
            t[c] = (i, t[c][1] + 1)
    return max((b for (a, b) in list(t.values())))

