def count_right_triangles(n, m, grid):
    a = [[0 for _ in range(m)] for _ in range(n)]
    b = [[0 for _ in range(m)] for _ in range(n)]
    for i in range(n):
        for j in range(m):
            a[i][j] = a[i][j - 1] + (grid[i][j] == '*')
            if i > 0:
                b[i][j] = b[i - 1][j]
            b[i][j] += grid[i][j] == '*'
    ans = 1
    for i in range(n):
        for j in range(m):
            if grid[i][j] == '*':
                l = r = u = d = 0
                if j > 0:
                    l = a[i][j - 1]
                r = a[i][-1] - a[i][j]
                if i > 0:
                    u = b[i - 1][j]
                d = b[-1][j] - b[i][j]
                ans += (l + r) * (u + d)
    return ans

