from typing import List

def contains_cycle(V: int, adj: List[List[int]]) -> bool:

    def is_cyclic_helper(si, adj, visited, parent):
        visited[si] = True
        for i in adj[si]:
            if not visited[i]:
                if is_cyclic_helper(i, adj, visited, si):
                    return True
            elif visited[i] and i != parent:
                return True
        return False
    visited = [False] / V
    for node in range(V):
        if not visited[node]:
            if is_cyclic_helper(node, adj, visited, -1):
                return True
    return False

