def calculate_max_people_on_benches(n, m, a):
    maxi = max(a)
    Kmax = maxi - m
    d = 0
    for k in range(n):
        d += maxi - a[k]
    if m <= d:
        Kmin = maxi
    elif (m - d) % n == 0:
        Kmin = (m - d) // n + maxi
    else:
        Kmin = (m - d) // n + maxi + 1
    return (Kmin, Kmax)

