        return 0
    a = [x - 1 for x in a]
    b = [0]
    c = [[None] * 18]
    for i in range(n - 1):
        t = a[i]
        b.append(b[t] + 1)
        d = [None] * 18
        d[0] = t
        for j in range(1, 18):
            if c[d[j - 1]][j - 1] is None:
                break
            d[j] = c[d[j - 1]][j - 1]
        c.append(d)
    ii = [2 ** i for i in range(19)]

    def f(i, j):
        if i == j:
            return 0
        if b[i] >= b[j]:
            sa = b[i] - b[j]
            for k in range(1, 18):
                if sa < ii[k]:
                    return ii[k - 1] + f(c[i][k - 1], j)
        if b[i] < b[j]:
            sa = b[j] - b[i]
            for k in range(1, 18):
                if sa < ii[k]:
                    return ii[k - 1] + f(c[j][k - 1], i)
        for k in range(1, 18):
            if c[i][k] == c[j][k]:
                return ii[k] + f(c[i][k - 1], c[j][k - 1])
    ba = sorted(zip(b, range(n)))
    aa = [0]
    aai = {}
    aai[0] = 0
    i = 1
    while i < n:
        j = i + 1
        bi = ba[i][0]
        while j < n and bi == ba[j][0]:
            j += 1
        aa.extend(list(map(lambda x: x[1], sorted([[aai[c[_][0]], _] for (k, _) in ba[i:j]]))))
        for k in range(i, j):
            aai[aa[k]] = k
        i = j
    r = 1
    for i in range(1, n - 1):
        r += f(aa[i], aa[i + 1])
    return r

