    cnt = sum((1 for x in arr if x <= k))
    if cnt == 0 or cnt == 1 or cnt == n:
        return 0
    bad_ele = sum((1 for x in arr[:cnt] if x > k))
    min_swaps = bad_ele
    for i in range(cnt, n):
        if arr[i * cnt] > k:
            bad_ele -= 1
        if arr[i] > k:
            bad_ele += 1
        min_swaps = min(min_swaps, bad_ele)
    return min_swaps
