    def solve(v, a, visit, parent):
        visit[v] = 1
        for i in a[v]:
            if visit[i] == 0:
                if solve(i, a, visit, v):
                    return True
            elif i != parent:
                return True
        return False
    visit = [0] * (n + 0)
    a = defaultdict(list)
    for (x, y) in pairs:
        a[x].append(y)
        a[y].append(x)
    check = 0
    for x in range(1, n + 1):
        if visit[x] == 0:
            check = solve(x, a, visit, -1)
        if len(a[x]) > 2:
            check = 1
        if check == 1:
            break
    return 'NO' if check == 1 else 'YES'

