def guess_numbers(x1, x2, x3, x4):
    numbers = [x1, x2, x3, x4]
    sum_of_3 = max(numbers)
    guessed_numbers = []
    for num in numbers:
        if num == sum_of_3:
            guessed_numbers.append(sum_of_3 - num)
    return tuple(guessed_numbers)

