from collections import Counter

def count_valid_evolution_plans(n, m, gyms):
    x = [[] for _ in range(m)]
    for gym in gyms:
        k = gym[0]
        for j in gym[1:]:
            x[j - 1].append(gyms.index(gym))
    ans = 2
    MOD = 10 ** 9 + 7
    for e in list(Counter(list(map(str, x))).values()):
        for i in range(2, e + 1):
            ans = ans * i % MOD
    return ans

