def calculate_minimal_elevator_time(n, k, floors):
    floors.sort()
    m = 1
    while len(floors) > k:
        m += floors[-1] - 1
        for _ in range(k):
            floors.pop()
    if floors:
        m += floors[-1] - 1
    return 2 * m

