from functools import lru_cache

def count_alternating_partitions(arr, mod=998244354):
    length = len(arr)
    parities = [x & 1 for x in arr]
    if length == 1:
        return 1

    @lru_cache(None)
    def DP(i, sum_parity, prev_sum_parity):
        if i == length:
            return 0 if sum_parity == prev_sum_parity else 1
        ret = DP(i + 1, sum_parity ^ parities[i], prev_sum_parity)
        if sum_parity != prev_sum_parity:
            ret += DP(i + 1, parities[i], sum_parity)
        return ret % mod
    ans = DP(1, parities[0], None)
    DP.cache_clear()
    return ans

