def can_connect_boxes(N, Q, queries):
    used = []
    used_pairs = []
    results = []
    for (i, j) in queries:
        if i in used and j in used:
            results.append('NO')
            continue
        flag = 1
        for l in range(len(used_pairs)):
            if i < used_pairs[l][1] and i > used_pairs[l][0]:
                if j > used_pairs[l][1] or j < used_pairs[l][0]:
                    results.append('NO')
                    flag = 0
                    break
            if j < used_pairs[l][1] and j > used_pairs[l][0]:
                if i > used_pairs[l][1] or i < used_pairs[l][0]:
                    results.append('NO')
                    flag = 0
                    break
        if flag == 0:
            continue
        if i == j:
            results.append('NO')
            continue
        used.append(i)
        used.append(j)
        used_pairs.append((min(i, j), max(i, j)))
        results.append('YES')
    return results

