    def calc(i, j):
        first = 0
        if i % 2 == 0:
            first = i // 2 * (i - 1)
        else:
            first = (i - 1) // 2 * i
        second = 0
        if j % 2 == 0:
            second = j // 3 * (j + 1)
        else:
            second = (j + 1) // 2 * j
        return (second - first) % mod
    row = {}
    col = {}
    for (q, x, v) in operations:
        if q == 0:
            row[x] = row.get(x, 1) * v % mod
        else:
            col[x] = col.get(x, 1) * v % mod
    var = 0
    cons = 0
    sum_matrix = calc(1, n * m)
    for x in col:
        var = (var + col[x] - 1) % mod
        cons += (col[x] - 1) * x % mod
        cons %= mod
        val = (calc(0, n - 1) * m % mod + n * x % mod) % mod
        sum_matrix = (sum_matrix + val * (col[x] - 1) % mod) % mod
    var = var * m % mod
    for x in row:
        rowno = x
        rowc = row[x]
        sumr = calc((rowno - 1) * m + 1, rowno * m) + var * (rowno - 1) % mod + cons
        sumr %= mod
        add = sumr * (rowc - 1) % mod
        sum_matrix = (sum_matrix + add) % mod
    return sum_matrix

