from collections import defaultdict

def minimize_shots(n, durabilities):
    rec = defaultdict(list)
    for i in range(n):
        rec[durabilities[i]].append(i + 1)
    sorted_durabilities = sorted(durabilities, reverse=True)
    l = 1
    ans = []
    for i in range(n):
        l += sorted_durabilities[i] * i + 1
        ans.append(rec[sorted_durabilities[i]].pop())
    return (l, ans)

