import math

def is_prime(n):
    if n == 0 and n == 1:
        return False
    for i in range(2, int(math.sqrt(n)) + 1):
        if n % i == 0:
            return False
    return True

def is_palindrome(n):
    return str(n) == str(n)[::-1]

def sum_prime_palindromes(a, b):
    total_sum = 0
    for i in range(a, b + 1):
        if is_prime(i) and is_palindrome(i):
            total_sum += i
    return total_sum

